import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { motion } from "framer-motion";
import { z } from "zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { ResourceWithStatus } from "@/lib/types";
import { Calendar, Clock, Users, MapPin, X } from "lucide-react";

interface BookingModalProps {
  isOpen: boolean;
  onClose: () => void;
  resource: ResourceWithStatus | null;
}

const bookingSchema = z.object({
  startTime: z.string().min(1, "Start time is required"),
  endTime: z.string().min(1, "End time is required"),
  purpose: z.string().min(1, "Purpose is required").max(200, "Purpose must be less than 200 characters"),
  attendees: z.number().min(1, "At least 1 attendee required").max(500, "Too many attendees"),
});

type BookingFormData = z.infer<typeof bookingSchema>;

export function BookingModal({ isOpen, onClose, resource }: BookingModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: currentUser } = useQuery<any>({
    queryKey: ["/api/auth/me"],
  });

  const form = useForm<BookingFormData>({
    resolver: zodResolver(bookingSchema),
    defaultValues: {
      startTime: "",
      endTime: "",
      purpose: "",
      attendees: 1,
    },
  });

  const bookingMutation = useMutation({
    mutationFn: async (data: BookingFormData) => {
      if (!resource) throw new Error("No resource selected");

      if (!currentUser) {
        throw new Error("User not authenticated");
      }

      return apiRequest("POST", "/api/bookings", {
        resourceId: resource.id,
        userId: currentUser.id,
        startTime: new Date(data.startTime).toISOString(),
        endTime: new Date(data.endTime).toISOString(),
        purpose: data.purpose,
        attendees: data.attendees,
      });
    },
    onSuccess: () => {
      toast({
        title: "Booking Created Successfully",
        description: `Your booking for ${resource?.name} has been confirmed.`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      onClose();
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Booking Failed",
        description: error.message || "Failed to create booking. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: BookingFormData) => {
    bookingMutation.mutate(data);
  };

  if (!resource) return null;

  // Get today's date and time for min datetime
  const now = new Date();
  const today = now.toISOString().slice(0, 16);

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl" data-testid="booking-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <span>Book {resource.name}</span>
            <Button variant="ghost" size="sm" onClick={onClose} data-testid="button-close-booking-modal">
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Resource Info */}
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-muted/50 rounded-lg p-4"
          >
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
              <div>
                <p className="text-muted-foreground flex items-center gap-1">
                  <Users className="w-3 h-3" />
                  Capacity
                </p>
                <p className="font-medium">{resource.capacity} people</p>
              </div>
              <div>
                <p className="text-muted-foreground">Department</p>
                <p className="font-medium">{resource.department?.name}</p>
              </div>
              <div>
                <p className="text-muted-foreground flex items-center gap-1">
                  <MapPin className="w-3 h-3" />
                  Location
                </p>
                <p className="font-medium text-xs">{resource.location}</p>
              </div>
              <div>
                <p className="text-muted-foreground">Status</p>
                <span className={`inline-block px-2 py-1 rounded-full text-xs font-medium ${
                  resource.status === 'available' ? 'bg-success text-success-foreground' :
                  resource.status === 'booked' ? 'bg-warning text-warning-foreground' :
                  resource.status === 'ongoing' ? 'bg-destructive text-destructive-foreground' :
                  'bg-secondary text-secondary-foreground'
                }`}>
                  {resource.status}
                </span>
              </div>
            </div>

            {/* Working Hours Info */}
            {resource.hasWorkingHours && (
              <div className="mt-3 pt-3 border-t border-border/50">
                <p className="text-muted-foreground flex items-center gap-1 text-sm">
                  <Clock className="w-3 h-3" />
                  Working Hours: {resource.workingHoursStart} - {resource.workingHoursEnd}
                </p>
              </div>
            )}
            {!resource.hasWorkingHours && (
              <div className="mt-3 pt-3 border-t border-border/50">
                <p className="text-muted-foreground flex items-center gap-1 text-sm">
                  <Clock className="w-3 h-3" />
                  Available 24/7
                </p>
              </div>
            )}
          </motion.div>

          {/* Booking Form */}
          <Form {...form}>
            <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="startTime"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        <Clock className="w-4 h-4" />
                        Start Time
                      </FormLabel>
                      <FormControl>
                        <Input
                          type="datetime-local"
                          min={today}
                          {...field}
                          data-testid="input-start-time"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="endTime"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        <Clock className="w-4 h-4" />
                        End Time
                      </FormLabel>
                      <FormControl>
                        <Input
                          type="datetime-local"
                          min={today}
                          {...field}
                          data-testid="input-end-time"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="attendees"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Users className="w-4 h-4" />
                      Number of Attendees
                    </FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        min={1}
                        max={resource.capacity}
                        placeholder={`Max ${resource.capacity} people`}
                        {...field}
                        onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                        data-testid="input-booking-attendees"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="purpose"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Purpose of Booking</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Brief description of how you'll use this resource..."
                        className="resize-none"
                        rows={3}
                        {...field}
                        data-testid="textarea-booking-purpose"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex justify-end space-x-3 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={onClose}
                  data-testid="button-cancel-booking"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={bookingMutation.isPending}
                  data-testid="button-confirm-booking"
                >
                  {bookingMutation.isPending ? "Creating..." : "Confirm Booking"}
                </Button>
              </div>
            </form>
          </Form>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/booking-slot.tsx.content:
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import type { BookingWithResource } from "@/lib/types";
import { Clock, User, MapPin } from "lucide-react";

interface BookingSlotProps {
  booking: BookingWithResource;
  onClick?: (booking: BookingWithResource) => void;
}

const statusConfig = {
  confirmed: {
    label: "Confirmed",
    className: "bg-success text-success-foreground",
  },
  pending: {
    label: "Pending",
    className: "bg-warning text-warning-foreground",
  },
  ongoing: {
    label: "In Progress",
    className: "bg-destructive text-destructive-foreground",
  },
  completed: {
    label: "Completed",
    className: "bg-muted text-muted-foreground",
  },
  cancelled: {
    label: "Cancelled",
    className: "bg-secondary text-secondary-foreground",
  },
};

export function BookingSlot({ booking, onClick }: BookingSlotProps) {
  const config = statusConfig[booking.status as keyof typeof statusConfig];

  const formatTime = (date: Date) => {
    return new Intl.DateTimeFormat('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
    }).format(new Date(date));
  };

  const formatTimeRange = () => {
    const start = formatTime(booking.startTime);
    const end = formatTime(booking.endTime);
    return `${start} - ${end}`;
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      whileHover={{ scale: 1.02 }}
      className="booking-slot bg-muted/50 rounded-lg p-4 hover:bg-muted transition-colors cursor-pointer"
      onClick={() => onClick?.(booking)}
      data-testid={`booking-slot-${booking.id}`}
    >
      <div className="flex items-center justify-between mb-2">
        <span className="font-medium text-card-foreground" data-testid={`text-booking-resource-${booking.id}`}>
          {booking.resource?.name || "Unknown Resource"}
        </span>
        <span 
          className={cn("text-xs px-2 py-1 rounded-full", config.className)}
          data-testid={`status-booking-${booking.status}-${booking.id}`}
        >
          {config.label}
        </span>
      </div>

      <div className="text-sm text-muted-foreground space-y-1">
        <div className="flex items-center space-x-2">
          <Clock className="w-3 h-3" />
          <span data-testid={`text-booking-time-${booking.id}`}>
            {formatTimeRange()}
          </span>
        </div>

        {booking.purpose && (
          <div className="flex items-start space-x-2">
            <User className="w-3 h-3 mt-0.5" />
            <span className="truncate" data-testid={`text-booking-purpose-${booking.id}`}>
              {booking.purpose}
            </span>
          </div>
        )}

        {booking.resource?.location && (
          <div className="flex items-center space-x-2">
            <MapPin className="w-3 h-3" />
            <span className="text-xs" data-testid={`text-booking-location-${booking.id}`}>
              {booking.resource.location}
            </span>
          </div>
        )}
      </div>

      {booking.attendees && (
        <div className="mt-2 pt-2 border-t border-border">
          <span className="text-xs text-muted-foreground" data-testid={`text-booking-attendees-${booking.id}`}>
            {booking.attendees} attendees
          </span>
        </div>
      )}
    </motion.div>
  );
}

client/src/components/quick-book-form.ts.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import type { QuickBookFormData } from "@/lib/types";

const quickBookSchema = z.object({
  resourceType: z.string().min(1, "Resource type is required"),
  date: z.string().min(1, "Date is required"),
  duration: z.number().min(1, "Duration must be at least 1 hour").max(8, "Duration cannot exceed 8 hours"),
  purpose: z.string().min(1, "Purpose is required").max(200, "Purpose must be less than 200 characters"),
  attendees: z.number().min(1, "At least 1 attendee is required").max(500, "Too many attendees"),
});

interface QuickBookFormProps {
  onSubmit: (data: QuickBookFormData) => void;
  isLoading?: boolean;
}

const resourceTypes = [
  { value: "computer_lab", label: "Computer Lab" },
  { value: "chemistry_lab", label: "Chemistry Lab" },
  { value: "physics_lab", label: "Physics Lab" },
  { value: "biology_lab", label: "Biology Lab" },
  { value: "auditorium", label: "Auditorium" },
  { value: "seminar_hall", label: "Seminar Hall" },
  { value: "sports_court", label: "Sports Court" },
];

const durations = [
  { value: 1, label: "1 hour" },
  { value: 2, label: "2 hours" },
  { value: 3, label: "3 hours" },
  { value: 4, label: "4 hours" },
  { value: 6, label: "6 hours" },
  { value: 8, label: "8 hours" },
];

export function QuickBookForm({ onSubmit, isLoading = false }: QuickBookFormProps) {
  const form = useForm<QuickBookFormData>({
    resolver: zodResolver(quickBookSchema),
    defaultValues: {
      resourceType: "",
      date: "",
      duration: 2,
      purpose: "",
      attendees: 1,
    },
  });

  const handleSubmit = (data: QuickBookFormData) => {
    onSubmit(data);
  };

  // Get today's date in YYYY-MM-DD format for min date
  const today = new Date().toISOString().split('T')[0];

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-card border border-border rounded-xl p-6"
    >
      <h3 className="text-lg font-semibold mb-4">Quick Book</h3>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
          <FormField
            control={form.control}
            name="resourceType"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Resource Type</FormLabel>
                <Select onValueChange={field.onChange} defaultValue={field.value}>
                  <FormControl>
                    <SelectTrigger data-testid="select-resource-type">
                      <SelectValue placeholder="Select resource type" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {resourceTypes.map((type) => (
                      <SelectItem 
                        key={type.value} 
                        value={type.value}
                        data-testid={`option-${type.value}`}
                      >
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="date"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Date</FormLabel>
                  <FormControl>
                    <Input
                      type="date"
                      min={today}
                      {...field}
                      data-testid="input-date"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="duration"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Duration</FormLabel>
                  <Select onValueChange={(value) => field.onChange(parseInt(value))} defaultValue={field.value.toString()}>
                    <FormControl>
                      <SelectTrigger data-testid="select-duration">
                        <SelectValue />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {durations.map((duration) => (
                        <SelectItem 
                          key={duration.value} 
                          value={duration.value.toString()}
                          data-testid={`option-duration-${duration.value}`}
                        >
                          {duration.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <FormField
            control={form.control}
            name="attendees"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Number of Attendees</FormLabel>
                <FormControl>
                  <Input
                    type="number"
                    min={1}
                    max={500}
                    {...field}
                    onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                    data-testid="input-attendees"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="purpose"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Purpose</FormLabel>
                <FormControl>
                  <Textarea
                    placeholder="Brief description of the booking purpose..."
                    className="resize-none"
                    {...field}
                    data-testid="textarea-purpose"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <Button
            type="submit"
            className="w-full"
            disabled={isLoading}
            data-testid="button-find-slots"
          >
            {isLoading ? "Searching..." : "Find Available Slots"}
          </Button>
        </form>
      </Form>
    </motion.div>
  );
}

client/src/components/resource-card.tsx.content:
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import type { ResourceWithStatus } from "@/lib/types";
import { 
  Computer, 
  FlaskConical, 
  Atom, 
  Dna, 
  Calculator, 
  Building, 
  Users, 
  MapPin,
  Clock,
  Theater,
} from "lucide-react";

interface ResourceCardProps {
  resource: ResourceWithStatus;
  onBook: (resource: ResourceWithStatus) => void;
  onViewSchedule: (resource: ResourceWithStatus) => void;
}

const resourceTypeIcons = {
  computer_lab: Computer,
  chemistry_lab: FlaskConical,
  physics_lab: Atom,
  biology_lab: Dna,
  auditorium: Theater,
  seminar_hall: Building,
  sports_court: Users,
};

const statusConfig = {
  available: {
    label: "Available",
    className: "status-available",
    nextAvailable: "Now",
    nextAvailableClass: "text-success",
    actionLabel: "Book Now",
    actionVariant: "default" as const,
  },
  booked: {
    label: "Booked",
    className: "status-booked", 
    nextAvailable: "3:00 PM",
    nextAvailableClass: "text-warning",
    actionLabel: "View Schedule",
    actionVariant: "secondary" as const,
  },
  ongoing: {
    label: "Ongoing",
    className: "status-ongoing",
    nextAvailable: "5:00 PM", 
    nextAvailableClass: "text-destructive",
    actionLabel: "Join Queue",
    actionVariant: "secondary" as const,
  },
  maintenance: {
    label: "Maintenance",
    className: "status-acquired",
    nextAvailable: "Tomorrow",
    nextAvailableClass: "text-muted-foreground",
    actionLabel: "Notify Me",
    actionVariant: "secondary" as const,
  },
};

export function ResourceCard({ resource, onBook, onViewSchedule }: ResourceCardProps) {
  const IconComponent = resourceTypeIcons[resource.type as keyof typeof resourceTypeIcons] || Building;
  const config = statusConfig[resource.status];

  const handleAction = () => {
    if (resource.status === "available") {
      onBook(resource);
    } else {
      onViewSchedule(resource);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={{ y: -2 }}
      className="resource-card bg-card border border-border rounded-xl p-6 cursor-pointer transition-all"
      data-testid={`card-resource-${resource.id}`}
    >
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center space-x-3">
          <div className="w-12 h-12 bg-blue-500/10 rounded-lg flex items-center justify-center">
            <IconComponent className="text-blue-500 w-6 h-6" />
          </div>
          <div>
            <h4 className="font-semibold text-card-foreground" data-testid={`text-resource-name-${resource.id}`}>
              {resource.name}
            </h4>
            <p className="text-sm text-muted-foreground" data-testid={`text-resource-department-${resource.id}`}>
              {resource.department?.name || "Unknown Department"}
            </p>
          </div>
        </div>
        <span 
          className={cn("px-3 py-1 rounded-full text-xs font-medium", config.className)}
          data-testid={`status-${resource.status}-${resource.id}`}
        >
          {config.label}
        </span>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
        <div>
          <p className="text-muted-foreground flex items-center gap-1">
            <Users className="w-3 h-3" />
            Capacity
          </p>
          <p className="font-medium" data-testid={`text-capacity-${resource.id}`}>
            {resource.capacity} {resource.type.includes('lab') ? 'stations' : 'seats'}
          </p>
        </div>
        <div>
          <p className="text-muted-foreground">Equipment</p>
          <p className="font-medium truncate" data-testid={`text-equipment-${resource.id}`}>
            {resource.equipment?.slice(0, 2).join(", ") || "Standard equipment"}
          </p>
        </div>
        <div>
          <p className="text-muted-foreground flex items-center gap-1">
            <Clock className="w-3 h-3" />
            Next Available
          </p>
          <p className={cn("font-medium", config.nextAvailableClass)} data-testid={`text-next-available-${resource.id}`}>
            {config.nextAvailable}
          </p>
        </div>
        <div className="flex items-end">
          <Button
            onClick={handleAction}
            variant={config.actionVariant}
            size="sm"
            className="w-full text-sm"
            data-testid={`button-${resource.status === 'available' ? 'book' : 'schedule'}-${resource.id}`}
          >
            {config.actionLabel}
          </Button>
        </div>
      </div>

      {resource.location && (
        <div className="mt-4 pt-4 border-t border-border">
          <p className="text-xs text-muted-foreground flex items-center gap-1">
            <MapPin className="w-3 h-3" />
            {resource.location}
          </p>
        </div>
      )}
    </motion.div>
  );
}

client/src/components/sidebar.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";
import type { Department } from "@shared/schema";
import type { NavItem } from "@/lib/types";
import { 
  University, 
  LayoutDashboard, 
  Calendar, 
  Search, 
  X, 
  Menu,
  Computer,
  FlaskConical,
  Atom,
  Dna,
  Calculator,
  Building,
} from "lucide-react";

interface SidebarProps {
  isOpen: boolean;
  onToggle: () => void;
  onClose: () => void;
}

const departmentIcons = {
  "fas fa-desktop": Computer,
  "fas fa-flask": FlaskConical,
  "fas fa-atom": Atom,
  "fas fa-dna": Dna,
  "fas fa-calculator": Calculator,
  "fas fa-building": Building,
};

const mainNavItems: NavItem[] = [
  {
    id: "dashboard",
    label: "Dashboard",
    icon: "LayoutDashboard",
    path: "/",
    isActive: true,
  },
  {
    id: "bookings",
    label: "My Bookings", 
    icon: "Calendar",
    path: "/my-bookings",
  },
  {
    id: "browse",
    label: "Browse Resources",
    icon: "Search",
    path: "/browse-resources",
  },
];

export function Sidebar({ isOpen, onToggle, onClose }: SidebarProps) {
  const [location] = useLocation();
  const [activeItem, setActiveItem] = useState("dashboard");

  const { data: departments = [] } = useQuery<Department[]>({
    queryKey: ["/api/departments"],
  });

  const { data: currentUser } = useQuery<any>({
    queryKey: ["/api/auth/me"],
  });

  const sidebarVariants = {
    open: { x: 0 },
    closed: { x: "-100%" },
  };

  return (
    <>
      {/* Mobile overlay */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-40 bg-black/20 mobile-menu-overlay lg:hidden"
            onClick={onClose}
            data-testid="sidebar-overlay"
          />
        )}
      </AnimatePresence>

      {/* Sidebar */}
      <motion.aside
        variants={sidebarVariants}
        animate={isOpen ? "open" : "closed"}
        transition={{ type: "spring", damping: 30, stiffness: 300 }}
        className="fixed left-0 top-0 z-50 h-screen w-72 bg-sidebar border-r border-sidebar-border sidebar-transition lg:translate-x-0"
        data-testid="sidebar"
      >
        <div className="flex h-full flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-sidebar-border">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-sidebar-primary rounded-lg flex items-center justify-center">
                <University className="text-sidebar-primary-foreground w-5 h-5" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-sidebar-foreground">UniResource</h1>
                <p className="text-sm text-muted-foreground">Resource Manager</p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="lg:hidden p-2 hover:bg-sidebar-accent rounded-md transition-colors"
              data-testid="button-close-sidebar"
            >
              <X className="text-sidebar-foreground w-4 h-4" />
            </button>
          </div>

          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-2">
            <div className="space-y-1">
              {mainNavItems.map((item) => {
                const isActive = location === item.path;
                const Icon = item.icon === "LayoutDashboard" ? LayoutDashboard : 
                           item.icon === "Calendar" ? Calendar : Search;

                return (
                  <Link
                    key={item.id}
                    href={item.path || "#"}
                    onClick={() => {
                      setActiveItem(item.id);
                      if (window.innerWidth < 1024) onClose();
                    }}
                  >
                    <motion.button
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                      className={cn(
                        "w-full flex items-center space-x-3 px-4 py-3 text-left rounded-lg transition-colors",
                        isActive
                          ? "bg-sidebar-primary text-sidebar-primary-foreground"
                          : "text-muted-foreground hover:bg-sidebar-accent hover:text-sidebar-accent-foreground"
                      )}
